import React, { useState } from "react";
import styles from "./Login.module.css";
import { Container, Row, Col } from "react-bootstrap";
import { useForm } from "react-hook-form";
import { Link, useNavigate } from "react-router-dom";
import Example from "../../components/oading/Loading";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const axios = require("axios").default;

const Login = () => {
  // const [path, setPath] = useState("");
  const [loading, setLoading] = useState(false);
  const {
    register,
    handleSubmit,

    formState: { errors },
  } = useForm();
  const navigate = useNavigate();

  const postApi = async (data) => {
    try {
      setLoading(true);

      const result = await axios({
        method: "POST",
        url: "http://khanh.tokyo/api/login",
        data,
      });
      localStorage.setItem("infor", JSON.stringify(result.data.data));
      // console.log("day la result", result.data.data.name);

      setLoading(false);
      navigate("/");
    } catch (errors) {
      setLoading(false);
      toast("Tên đăng nhập hoặc mật khẩu không đúng !. Vui lòng kiểm tra lại");
    }
  };
  const onSubmit = (data) => {
    postApi(data);
  };

  return (
    <section className="h-100">
      <div className="container h-100">
        <div className="row justify-content-sm-center h-100">
          <div className="col-xxl-4 col-xl-5 col-lg-5 col-md-7 col-sm-9">
            <div className="text-center my-5">
              <h1>Login </h1>
            </div>
            <div className="card shadow-lg">
              <div className="card-body p-5">
                {loading ? (
                  <div className="text-center d-flex justify-content-center">
                    <ReactLoading
                      type="spin"
                      color="blue"
                      height={"20%"}
                      width={"20%"}
                    />
                  </div>
                ) : (
                  <LoginForm onSubmit={onSubmit} />
                )}
              </div>
              <div className="card-footer py-3 border-0">
                <div className="text-center">
                  Already have an account?{" "}
                  <Link to="/register" className="text-dark">
                    Register
                  </Link>
                </div>
              </div>
            </div>
            <div className="text-center mt-5 text-muted">
              Copyright © Your Company
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default LoginPage;
