                  return (
                    <Link
                      to={`product/${product.id}/${product.slug}`}
                      className={`card ${styles.cardSize} shadow text-decoration-none text-black mt-4 rounded  mx-2`}
                      key={i}
                    >
                      <img
                        src={product.avatar}
