import React, { useEffect, useState } from "react";
import { Col, Container, Row } from "react-bootstrap";
import serviceCallApi from "../../services/ServiceApi";
import styles from "../home/HomePage.module.css";
import Header from "../../components/Layout/DefaultLayout/Header";
import { Link } from "react-router-dom";
import { BsArrowUpSquare } from "react-icons/bs";
import Banner from "../../components/Banner/Banner.js";
import "../../App.css";
import Footer from "../../components/Footer/Footer";

const axios = require("axios").default;

const pages = [1, 2, 3];

const HomePage = () => {  
  const [product, setProduct] = useState([]);
  const [page, setPage] = useState(1);
  useEffect(() => {
    const handleScrol = (e) => {
      const onTop = document.getElementById("onTheTop");
      if (window.scrollY > 100) {
        onTop.classList.remove("d-none");
      } else {
        onTop.classList.add("d-none");
      }
    };
    window.addEventListener("scroll", handleScrol);
  }, []);
  const handleNextPage = (i) => {
    setPage(i + 1);

    const animation = setInterval(() => {
      document.body.scrollTop = 100;
      document.documentElement.scrollTop = 100;
      console.log("log");
      clearInterval(animation, 200);
    }, 200);
 
  };



  const onTop = (e) => {
    document.body.scrollTop = 0;
    document.documentElement.scrollTop = 0;
  };

  useEffect(() => {
    getProductList();
  }, []);
  const [data, setData] = useState([]);

  const getProductList = async () => {
    const categoryId = 2;
    const response = await serviceCallApi(
      `products?page=1&limit=10&id=${categoryId}`,
      "GET"
    );
    console.log(
      "🚀 ~ file: HomePage.js ~ line 28 ~ getProductList ~ response",
      response
    );
    setData(response.data.data);
  };

  const renderProduct = () => {
    return data.map((product, index) => {
      return (
        <div className="row" key={index}>
          <div className="col-4">
            <Link to={`/${product.id}/${product.slug}`}>
              <h2> {product.name}</h2>
              <img
                src={product.avatar}
                className="img-thumbnail"
                alt={product.name}
              />
            </Link>
            {product.price}
          </div>
        </div>
      );
    });
  };
const LayoutHomePage = () => {
  // console.log(product);
  return (
    <div>
      <Header />
      <Container>
        <Row>
          <Col>
          <div className="carousel-inner mt-2 ">
          <div className="carousel-item active" data-bs-interval="3000">
            <img src="https://cf.shopee.vn/file/46ac0298c25f797c33189c6148ee8221" className="d-block  img-fluid" alt="..." />
          </div>
          
        </div>
            <div className="d-none d-md-block">
              <ul className="list-unstyled py-4 d-flex justify-content-between ">
              <li className="d-flex flex-column align-items-center justify-content-start">
                  <img
                    src="https://cf.shopee.vn/file/b3535d7e56c58c4ebe9a87672d38cc5e_xhdpi"
                    alt=""
                    className={`${styles.sp}`}
                  />
                  <span className={`text-center mt-2 ${styles.fz}`}>
                    Gì cũng rẻ - <br /> Mua là Freeship
