import React, { useState } from "react";
import styles from "./Login.module.css";
import { Container, Row, Col } from "react-bootstrap";
import { useForm } from "react-hook-form";
import { Link, useNavigate } from "react-router-dom";
import Example from "../../components/loading/Loading";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
const axios = require("axios").default;

const Login = () => {
  // const [path, setPath] = useState("");
  const [loading, setLoading] = useState(false);
  const {
    register,
    handleSubmit,

    formState: { errors },
  } = useForm();
  const navigate = useNavigate();

  const postApi = async (data) => {
    try {
      setLoading(true);

      const result = await axios({
        method: "POST",
        url: "http://khanh.tokyo/api/login",
        data,
      });
      localStorage.setItem("infor", JSON.stringify(result.data.data));
      // console.log("day la result", result.data.data.name);

      setLoading(false);
      navigate("/");
    } catch (errors) {
      setLoading(false);
      toast("Tên đăng nhập hoặc mật khẩu không đúng !. Vui lòng kiểm tra lại");
    }
  };
  const onSubmit = (data) => {
    postApi(data);
  };

  return (

          <div
            className={`d-flex flex-column  justify-content-center align-content-center ${styles.Login}`}
          >
            <h1 className="text-center mb-5">Login to continue</h1>
            {loading ? (
              <Example />
            ) : (
              <div>
      <form
        onSubmit={handleSubmit(props.onSubmit)}
        className="needs-validation"
      >
        <div className="mb-3">
          <label className="mb-2 text-muted" htmlFor="email">
            E-Mail Address
          </label>
          <input
            id="email"
            type="email"
            className="form-control"
            name="email"
            {...register("email", { required: true })}
          />
          <div className="text-danger">
            {errors.email && <span>This field is required</span>}
          </div>
        </div>
        <div className="mb-3">
          <label className="mb-2 text-muted" htmlFor="password">
            Password
          </label>
          <input
            id="password"
            type="password"
            className="form-control"
            name="password"
            {...register("password", { required: true })}
          />
          <div className="text-danger">
            {" "}
            {errors.password && <span>This field is required</span>}
          </div>
        </div>
        <div className="align-items-center d-flex">
          <button type="submit" className="btn btn-primary ms-auto">
            Login
          </button>
        </div>
        </form>
        </div>
            )}
          </div>        

  );
};

export default Login;
